import React from 'react';
import { useLocation } from 'react-router-dom';
import '../pages/PerfumeDetail.css';

export default function PerfumeDetail() {
    const location = useLocation();
    const { detail } = location.state || {};

    if (!detail) {
        return <div>Loading...</div>;
    }

    const topNotes = detail.top_note_names ? detail.top_note_names.join(', ') : '';
    const middleNotes = detail.middle_note_names ? detail.middle_note_names.join(', ') : '';
    const baseNotes = detail.base_note_names ? detail.base_note_names.join(', ') : '';
    const singleNotes = detail.single_note_names ? detail.single_note_names.join(', ') : '';

    return (
        <div>
            <section className='perfume-img-section'>
                <img className='perfume-img' src={detail.image_path} alt={detail.perfume_name} />
                <section className='perfume-text'>
                    <h3 className='perfume-header'>{detail.perfume_name}</h3>
                    <p className='perfume-text'>탑 노트 : {topNotes}</p>
                    <p className='perfume-text'>미들 노트 : {middleNotes}</p>
                    <p className='perfume-text'>베이스 노트 : {baseNotes}</p>
                    <p className='perfume-text'>싱글 노트 : {singleNotes}</p>
                </section>
            </section>
        </div>   
    );
}
