import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

export default function PerfumesDetial () {
    const [items, setItems] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();


    const handleItemClick = async (uuid) => {
        try {
            const response = await fetch(`http://perfume-pedia.site:3000/api/search/advanced?${uuid}`);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const detailData = await response.json();
            navigate(`/culture/${uuid}`, { state: { detail: detailData.data } });
        } catch (err) {
            console.error("Error fetching details:", err);
        }
    };

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>

    return (
        <div>
            <section>
                <img src="" alt="" />
            </section>
            <section className='culture-section'>
                {items.map(item => (
                    <div key={item.uuid} onClick={() => handleItemClick(item.uuid)} style={{ cursor: 'pointer' }}>
                        <img className='culture-section-img' src={item.thumbnail} alt={item.place_name}/>
                    </div>
                ))}
            </section>
        </div>
    );
};
