import React from 'react';
import { useLocation } from 'react-router-dom';
import '../pages/PerfumeDetail.css';

export default function PerfumeDetail() {
    const location = useLocation();
    const { detail } = location.state || {};

    if (!detail) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <section className='perfume-section'>
                <img className='perfume-img' src={detail.image_path} alt={detail.perfume_name} />
                <h3 className='placename-header'>{detail.perfume_name}</h3>
                <p className='place-text'>{detail.top_note_names}</p>
            </section>  
        </div>
    );
}
