import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import './Navbar.css';

export default function Navbar() {
    const [searchTerm, setSearchTerm] = useState("");
    const navigate = useNavigate();
    const location = useLocation(); // ÌòÑÏû¨ ÏúÑÏπòÎ•º ÏïåÏïÑÎÇ¥Í∏∞ ÏúÑÌï¥ useLocation ÏÇ¨Ïö©

    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };

    const handleSearchSubmit = (event) => {
        event.preventDefault();
        navigate(`/search?keyword=${encodeURIComponent(searchTerm)}`);
        setSearchTerm("");
    };

    // Ìôà ÌôîÎ©¥Ïù∏ÏßÄ ÌôïÏù∏ÌïòÎäî Ìï®Ïàò
    const isHome = location.pathname === '/';

    return (
        <div className="navbar">
            <div className="navbar-logo">
                PerfumePediaüìì
            </div>
            {!isHome && ( // Ìôà ÌôîÎ©¥Ïù¥ ÏïÑÎãê ÎïåÎßå Í≤ÄÏÉâÏ∞Ω ÌëúÏãú
                <form onSubmit={handleSearchSubmit} className="search-form">
                    <input
                        type="text"
                        placeholder="Ìñ•Ïàò ÎòêÎäî ÏõêÌïòÎäî ÎÖ∏Ìä∏Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                        value={searchTerm}
                        onChange={handleSearchChange}
                        className="search-input"
                    />
                    <button type="submit" className="search-button">üîç</button>
                </form>
            )}
            <Link to="/" className="nav-link">Home</Link>
            <Link to="/notes" className="nav-link">Notes</Link>
            <Link to="/perfumes" className="nav-link">Perfumes</Link>
            {/* <Link to="/search" className="nav-link">Search</Link> */}
        </div>
    );
}
