import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

export default function Culturedetail() {
    const location = useLocation();
    const { detail } = location.state || {}; // 넘겨진 상태에서 상세 정보를 추출

    if (!detail) {
      return <div>Loading...</div>;
    }

    return (
      <div>
          <section className='place-section'>
            <img className='place-img' src={detail.thumbnail} alt={detail.place_name} />
            <h3 className='placename-header'>{detail.place_name}</h3>
            <p className='place-text'>{detail.address}</p>
          </section>  
      </div>
    );
  }
