import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

export default function Navbar() {
    const [searchTerm, setSearchTerm] = useState("");

    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };

    const handleSearchSubmit = (event) => {
        event.preventDefault();
        // Í≤ÄÏÉâ Î°úÏßÅÏùÑ Ïó¨Í∏∞Ïóê Ï∂îÍ∞ÄÌïòÏÑ∏Ïöî
        // ÏòàÎ•º Îì§Î©¥, Í≤ÄÏÉâ Í≤∞Í≥º ÌéòÏù¥ÏßÄÎ°ú Î¶¨ÎîîÎ†âÏÖò Ìï† Ïàò ÏûàÏäµÎãàÎã§:
        // props.history.push(`/search?query=${searchTerm}`);
        console.log('Searching for:', searchTerm);
    };

    return (
        <div className="navbar">
            <div className="navbar-logo">
                PerfumePediaüìì
            </div>
            <Link to="/" className="nav-link">Home</Link>
            <Link to="/notes" className="nav-link">Notes</Link>
            <Link to="/perfumes" className="nav-link">Perfumes</Link>
            <form onSubmit={handleSearchSubmit} className="search-form">
                <input
                    type="text"
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    className="search-input"
                />
                <button type="submit" className="search-button">üîç</button>
            </form>
        </div>
    );
};
