import React, { useState, useEffect } from 'react';
import '../pages/Perfumes.css';

export default function Search() {
    const [items, setItems] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                setIsLoading(true);
                const response = await fetch('http://49.50.162.194:3000/api/search?lastid=1234&size=10');

                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }

                const data = await response.json();
                setItems(data.data.items);
            } catch (err) {
                setError(err.message);
            } finally {
                setIsLoading(false);
            }
        };

        fetchData();
    }, []);
    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <section className="white">
                {items.map((item, index) => (
                    <div key={`${item.perfume_name}-${item.brand_name}-${index}`}>
                        <img src={item.image_url} alt={`${item.perfume_name} by ${item.brand_name}`} />
                    </div>
                ))}
            </section>
            <section className="grey">
                <p>검색 페이지</p>
            </section>
        </div>
    );
}
